{
    "name": "Disney",
    "enabled": false,
    "shouldRefresh": true,
    "shouldCheckInitialElements": false,
    "_comments": "isLikelySalesForce",
    "selectors": {
        "cartTotal": "div.cart-summary__value.grand-total",
        "couponRemove": "div.promo-code__item",
        "csrfToken": "input[name='csrf_token']"
    },
    "preCheckInitialElements": {
        "query:originalTotal:selector(cartTotal):text": true
    },
    "applyCoupon": {
        "set-session:prodege-currentCode:[code]": {
            "query:csrf_token:selector(csrfToken):attr(value)": {
                "http-get": {
                    "url": "https://www.shopdisney.com/on/demandware.store/Sites-shopDisney-Site/default/Cart-AddCoupon?couponCode=[code]&csrf_token=[var:csrf_token]",
                    "success": {
                        "if:hasJsonPath:[data].totals.grandTotal": {
                            "set-session:prodege-currentTotal:[data].totals.grandTotal": {
                                "set-session:prodege-uuid:[data].promotions[0].coupon.UUID": true
                            }
                        },
                        "if:!hasJsonPath:[data].totals.grandTotal": {
                            "set-session:prodege-currentTotal:[var:originalTotal]": {
                                "remove-session:prodege-uuid": true
                            }
                        }
                    }
                }
            }
        }
    },
    "removeCoupon": {
        "if:session:prodege-uuid": {
            "http-get": {
                "url": "https://www.shopdisney.com/on/demandware.store/Sites-shopDisney-Site/default/Cart-RemoveCouponLineItem?code=[session:prodege-currentCode]&uuid=[session:prodege-uuid]",
                "success": {
                    "set-dom": "[data]"
                }
            }
        }
    },
    "calculateTotal": {
        "set-query-result:[session:prodege-currentTotal]": true
    }
}

